#define MOD 1'000'000'007
#define LL long long int

class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int size = nums.size();
        vector<vector<vector<LL>>> dp(size, vector<vector<LL>>(51, vector<LL>(51, -1)));


        return static_cast<int>(f(nums, 0, 0, nums[0], dp));
    }

private:
    LL f(const vector<int>& nums, int index, int val1, int val2, vector<vector<vector<LL>>>& dp) {
        if (index >= nums.size()) return 1; 
        if (dp[index][val1][val2] != -1) return dp[index][val1][val2];  

        LL ans = 0;
        for (int i = val1; i <= nums[index]; ++i) {
            for (int j = val2; j >= 0; --j) {
                if (i + j == nums[index]) {
                    ans = (ans + f(nums, index + 1, i, j, dp)) % MOD;
                }
            }
        }
        dp[index][val1][val2] = ans;  
        return ans;
    }
};
