class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int MOD = 1e9 + 7;
        vector<vector<vector<int>>> memo(
            nums.size() + 1, vector<vector<int>>(51, vector<int>(51, -1)));
        return countPairs(0, 0, 50, nums, memo, MOD);
    }

private:
    int countPairs(int idx, int prevX, int prevY, const vector<int>& nums,
                   vector<vector<vector<int>>>& memo, int mod) {
        if (idx == nums.size())
            return 1;
        if (memo[idx][prevX][prevY] != -1)
            return memo[idx][prevX][prevY];

        int sum = 0, a = 0;
        while (a <= nums[idx]) {
            int b = nums[idx] - a;
            if (a >= prevX && b <= prevY) {
                sum = (sum + countPairs(idx + 1, a, b, nums, memo, mod)) % mod;
            }
            a++;
        }
        return memo[idx][prevX][prevY] = sum;
    }
};
