class Solution {
public:
    const long long int MOD=1e9+7; 
    int solve(vector<int>& nums,int i,int j,int k,int n,vector<vector<vector<int>>>&dp){
        if(i>=n){
            return 1;
        }
        if(dp[i][j][k]!=-1) return dp[i][j][k];
        int cnt=0;
        for(int a1=j;a1<=nums[i];a1++){
            int a2 = nums[i] - a1;
            if (a2 <= k) {
                cnt = (cnt + solve(nums,i+1,a1,a2,n,dp)) % MOD;
            }
        }
        return dp[i][j][k]=cnt;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        int maxi = *std::max_element(nums.begin(), nums.end());
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(maxi+1,vector<int>(maxi+1,-1)));
        int i=0,j=0,k=0;
        return solve(nums,i,j,maxi,n,dp);
    }
};
