class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10**9+7
        dp = [[0]*51 for _ in range(len(nums))]
        for i in range(len(nums)):
            for j in range(51):
                for k in range(51):
                    if k > j:
                        break
                    two = nums[i] - j
                    if two >= 0:
                        if i != 0:
                            pre = nums[i-1] - k
                            if pre >= two:
                                dp[i][j] += dp[i-1][k]
                        else:
                            dp[i][j] = 1
                    dp[i][j]%=mod
        return sum(dp[i])%mod
