class Solution {
public:
    map<pair<long long, long long>, long long> nextStep(long long total, map<pair<long long, long long>, long long> prev) {
        map<pair<long long, long long>, long long> curr;
        for (auto& p : prev) {
            long long left = p.first.first;
            long long right = p.first.second;
            for (long long i = left; i <= total; i++) {
                long long j = total - i;
                if (j <= right) {
                    if (curr.find({i,j}) == curr.end()) {curr[{i,j}]=0;}
                    curr[{i,j}] = (curr[{i,j}] + p.second) % 1000000007;
                }
            }
        }
        return curr;
    }

    int countOfPairs(vector<int>& nums) {
        map<pair<long long, long long>, long long> curr;
        curr[{0,51}]=1;
        for (long long step : nums) {
            curr = nextStep(step, curr);
        }
        long long sum = 0;
        for (auto& p : curr) {
            sum = (sum + p.second) % 1000000007;
        }
        return sum;
    }
};
