class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        M = int(1e9 + 7)
        N = len(nums)
        @cache
        def f(i, min1, max2):
            if i == N:
                return 1
            count = 0
            for j in range(min1, nums[i] + 1):
                comp = nums[i] - j
                if comp > max2:
                    continue
                count += f(i + 1, j, comp)
            return count % M
        return f(0, 0, M)
