import java.util.*;
class Solution {
    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long dp[][] = new long[n][], mod = 1000000007;

        for (int i = 0; i < n; i++) {
            dp[i] = new long[nums[i] + 1];
        }

        for (int i = 0; i <= nums[n - 1]; i++) {
            dp[n - 1][i] = ((long)1);
        }

        for (int i = n - 2; i >= 0; i--) {
            for (int j = 0; j <= nums[i]; j++) {
                int k = nums[i] - j;
                dp[i][j] = ((long)0);

                for (int m = j; m <= nums[i + 1]; m++) {
                    int nextK = nums[i + 1] - m;

                    if (k >= nextK) {
                        dp[i][j] = (dp[i][j] + dp[i + 1][m])%mod;
                    }
                }
            }
        }

        long result = 0;

        for (int i = 0; i <= nums[0]; i++) {
            result = (result + dp[0][i])%mod;
        }

        return (int)result;

    }
}
