class Solution {
public:
    int dp[2001][52][52];
    int mod = 1e9+7;
    int solve(int idx, vector<int>& nums, int prev1, int prev2) {
        if(idx==nums.size()) return 1;
        if(dp[idx][prev1][prev2]!=-1) return dp[idx][prev1][prev2];
        int count = 0;
        for(int i = prev1; i<=nums[idx]; i++) {
            int other = nums[idx] - i;
            if(other <= prev2) {
                count = (count + solve(idx+1, nums, i, other))%mod;
            }
        }
        return dp[idx][prev1][prev2] = count;
    }
    int countOfPairs(vector<int>& nums) {
        int n = nums.size();
        memset(dp, -1, sizeof(dp));
        return solve(0, nums, 0, 51);
    }
};
