class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        long long MOD = 1000000007;
        int n = nums.size();
        long long dp[n+1][51];
        memset(dp,0,sizeof(dp));
        for(int i=0;i<=nums[0];i++)dp[1][i] = 1LL;
        for(int i=2;i<=n;i++){
            for(int j=nums[i-1];j>=0;j--){
                int up = j;
                int down = nums[i-1]-j;
                for(int k = up;k<=50;k++) 
                if(down>=nums[i-2]-k && nums[i-2]-k>=0)(dp[i][up] += 1LL*dp[i-1][k])%=MOD;
            }
        }
        // for(int i=1;i<=n;i++){
        //     for(int j=0;j<=20;j++){
        //         cout<<dp[i][j]<<" ";
        //     }
        //     cout<<endl;

        // }

        long long ans = 0;
        for(int i=0;i<=50;i++){ans+=1LL*dp[n][i];ans%=MOD;}
        return ans;
    }
};
