#define MOD 1000000007
class Solution {
public:
    int solve(vector<int> &nums, int prev1, int prev2, int i, vector<vector<vector<int>>> &dp){
        if(i == nums.size()){
            return 1;
        }
        if(dp[i][prev1][prev2] != -1){
            return dp[i][prev1][prev2];
        }

        int ans = 0;
        for(int index = prev1; index <= nums[i]; index++){
            int y = nums[i] - index;

            if(y <= prev2){
                ans = (ans + solve(nums, index, y, i+1, dp)) % MOD;

            }
        }
        return dp[i][prev1][prev2] = ans;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size()+1, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(nums, 0, 50, 0, dp);
    }
};
