#define ll long long int
class Solution {
public:
 int countOfPairs(vector<int>&arr) {
     int len = arr.size();
        vector<vector<vector<ll>>>dp(len, vector<vector<ll>>(51, vector<ll>(51, -1)));

        function<ll(int, int, int)> computeWays = [&](int idx, int x, int y) -> ll {

              ll result = 0;
            ll MOD = 1e9 + 7;

            if (idx >= arr.size()) 
            return 1;

            if (dp[idx][x][y] != -1)
            return dp[idx][x][y];

            for (int a = x; a <= arr[idx]; a++) {
                for (int b = y; b >= 0; b--) {
                    if (a + b == arr[idx]) 
                        result = (result + computeWays(idx + 1, a, b)) % MOD;

                }
            }

            return dp[idx][x][y] = result;
        };

        int ans= computeWays(0, 0, arr[0]);

     return ans;
    }
};
