class Solution {
public:
    int dp[2005][51][51]; // Assuming maximum nums[ind] is 50
    const int MOD = 1e9 + 7;

    int func(int ind, int prev, vector<int>& arr1, vector<int>& arr2, vector<int>& nums) {
        if (ind >= nums.size()) {
            return 1;
        }

        // Using prev == -1 as a special case since we can't use it as an index
        int prevArr1 = (prev == -1) ? 0 : arr1[prev];
        int prevArr2 = (prev == -1) ? 0 : arr2[prev];

        if (dp[ind][prevArr1][prevArr2] != -1) {
            return dp[ind][prevArr1][prevArr2];
        }

        int ans = 0;
        for (int i = 0; i <= nums[ind]; i++) {
            int p1 = i;
            int p2 = nums[ind] - p1;
            if (prev == -1 || (prevArr1 <= p1 && prevArr2 >= p2)) {
                arr1.push_back(p1);
                arr2.push_back(p2);
                ans = (ans + func(ind + 1, ind, arr1, arr2, nums)) % MOD;
                arr1.pop_back();
                arr2.pop_back();
            }
        }

        return dp[ind][prevArr1][prevArr2] = ans;
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        vector<int> arr1, arr2;
        return func(0, -1, arr1, arr2, nums) % MOD;
    }
};
