import java.util.*;

class Solution {
    private long[][][] memo;
    private final long MOD = 1_000_000_007;

    private long solve(int idx, int a, int b, int[] nums) {
        if (idx >= nums.length) return 1;
        if (memo[idx][a][b] != -1) return memo[idx][a][b];

        long ans = 0;
        for (int i = a; i <= nums[idx]; i++) {
            for (int j = b; j >= 0; j--) {
                if (i + j == nums[idx]) {
                    ans = (ans + solve(idx + 1, i, j, nums)) % MOD;
                }
            }
        }

        return memo[idx][a][b] = ans;
    }

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        memo = new long[n][51][51];
        for (long[][] mat : memo) {
            for (long[] row : mat) {
                Arrays.fill(row, -1);
            }
        }
        return (int) solve(0, 0, nums[0], nums);
    }
}
