#define ll long long int
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int size = nums.size();
        vector<vector<vector<ll>>> memo(size, vector<vector<ll>>(51, vector<ll>(51, -1)));

        // Start the recursive function
        return countWays(0, 0, nums[0], nums, memo);
    }

private:
    const ll MOD = 1e9 + 7;

    ll countWays(int index, int prev1, int prev2, const vector<int>& nums, vector<vector<vector<ll>>>& memo) {
        if (index >= nums.size()) return 1;

        if (memo[index][prev1][prev2] != -1) return memo[index][prev1][prev2];

        ll result = 0;
        for (int i = prev1; i <= nums[index]; i++) {
            for (int j = prev2; j >= 0; j--) {
                if (i + j == nums[index]) {
                    result = (result + countWays(index + 1, i, j, nums, memo)) % MOD;
                }
            }
        }

        return memo[index][prev1][prev2] = result;
    }
};
