import java.util.Scanner;

class Solution {
    public int countOfPairs(int[] nums) {
        final int MOD = 1000000007;
        int n = nums.length;
        int maxVal = 50;


        int[][][] dp = new int[n][maxVal + 1][maxVal + 1];


        for (int j = 0; j <= maxVal; j++) {
            int k = nums[0] - j;
            if (k >= 0 && k <= maxVal) {
                dp[0][j][k] = 1;
            }
        }


        for (int i = 1; i < n; i++) {
            for (int j = 0; j <= maxVal; j++) {
                for (int k = 0; k <= maxVal; k++) {
                    if (dp[i - 1][j][k] > 0) {
                        for (int jNew = 0; jNew <= j; jNew++) {
                            int kNew = nums[i] - jNew;
                            if (kNew >= k && kNew <= maxVal) {
                                dp[i][jNew][kNew] = (dp[i][jNew][kNew] + dp[i - 1][j][k]) % MOD;
                            }
                        }
                    }
                }
            }
        }

        int r = 0;
        for (int j = 0; j <= maxVal; j++) {
            for (int k = 0; k <= maxVal; k++) {
                r = (r + dp[n - 1][j][k]) % MOD;
            }
        }

        return r;
    }


}
