class Solution {
    public int countOfPairs(int[] nums) {
        final int MOD = 1_000_000_007;
        int length = nums.length;
        int maxValue = 50;

        int[][][] dp = new int[length][maxValue + 1][maxValue + 1];

        for (int arr1Value = 0; arr1Value <= maxValue; arr1Value++) {
            int arr2Value = nums[0] - arr1Value;
            if (arr2Value >= 0 && arr2Value <= maxValue) {
                dp[0][arr1Value][arr2Value] = 1;
            }
        }

        for (int i = 1; i < length; i++) {
            for (int arr1Value = 0; arr1Value <= maxValue; arr1Value++) {
                for (int arr2Value = 0; arr2Value <= maxValue; arr2Value++) {
                    if (dp[i - 1][arr1Value][arr2Value] > 0) {
                        for (int newArr1Value = 0; newArr1Value <= arr1Value; newArr1Value++) {
                            int newArr2Value = nums[i] - newArr1Value;
                            if (newArr2Value >= arr2Value && newArr2Value <= maxValue) {
                                dp[i][newArr1Value][newArr2Value] = (dp[i][newArr1Value][newArr2Value] + dp[i - 1][arr1Value][arr2Value]) % MOD;
                            }
                        }
                    }
                }
            }
        }

        int totalCount = 0;
        for (int arr1Value = 0; arr1Value <= maxValue; arr1Value++) {
            for (int arr2Value = 0; arr2Value <= maxValue; arr2Value++) {
                totalCount = (totalCount + dp[length - 1][arr1Value][arr2Value]) % MOD;
            }
        }

        return totalCount;
    }
}
