#include <vector>
#include <functional>

#define ll long long
const ll MOD = 1'000'000'007;

class Solution {
public:
    int countOfPairs(std::vector<int>& values) {
        int size = values.size();
        std::vector<std::vector<std::vector<ll>>> memo(size, std::vector<std::vector<ll>>(51, std::vector<ll>(51, -1)));

        std::function<ll(int, int, int)> solve = [&](int index, int val1, int val2) -> ll {
            if (index >= size) return 1;
            if (memo[index][val1][val2] != -1) return memo[index][val1][val2];

            ll result = 0;
            for (int i = val1; i <= values[index]; ++i) {
                for (int j = val2; j >= 0; --j) {
                    if (i + j == values[index]) {
                        result = (result + solve(index + 1, i, j)) % MOD;
                    }
                }
            }

            return memo[index][val1][val2] = result;
        };

        return solve(0, 0, values[0]);
    }
};
