class Solution {
public:
    const int MOD = 1e9 + 7;

    int countValidPairs(int idx, int val1, int val2, const vector<int>& values, vector<vector<vector<int>>>& dp) {
        if (idx >= values.size()) return 1;
        if (dp[idx][val1][val2] != -1) return dp[idx][val1][val2];

        int res = 0;
        for (int i = val1; i <= values[idx]; i++) {
            for (int j = val2; j >= 0; j--) {
                if (i + j == values[idx]) {
                    res += countValidPairs(idx + 1, i, j, values, dp);
                    res %= MOD;
                }
            }
        }
        return dp[idx][val1][val2] = res;
    }

    int countOfPairs(vector<int>& values) {
        int size = values.size();
        vector<vector<vector<int>>> dp(size, vector<vector<int>>(51, vector<int>(51, -1)));
        return countValidPairs(0, 0, values[0], values, dp);
    }
};
