class Solution {

    long solve(int arr[], int i, int p1, int p2,long dp[][][])
    {
        if(i==arr.length)
            return 1;
        if(dp[i][p1][p2]!=-1)
            return dp[i][p1][p2];
        long  ans=0;
        for(int j=0;j<=arr[i];j++)
        {
            if(j<=p1&&(arr[i]-j)>=p2)
            {
                ans+=solve(arr, i+1, j, arr[i]-j,dp);
                ans%= (long)(1e9+7);
            }
        }
        return dp[i][p1][p2]=ans;

    }
    public int countOfPairs(int[] nums) {
        long dp[][][]=new long[nums.length+1][55][55];
        for(int i=0;i<=nums.length;i++)
        {
            for(int j=0;j<55;j++)
            {
                Arrays.fill(dp[i][j],-1);
            }
        }
        return (int)(solve(nums, 0, 51,0,dp));
    }
}
