class Solution {
public:
    int MOD=1e9+7;
    int solve(int n,int ind, int left, int right,vector<vector<vector<int>>>&dp, vector<int>&nums){
        if(ind==n)return 1;
        if(dp[ind][left][right]!=-1)return dp[ind][left][right];

        int p=nums[ind],x=0;
        for(int i=left; i<=p; i++){
            if((p-i)<=right)x=(x+solve(n,ind+1,i,p-i,dp,nums))%MOD;
        }
        return dp[ind][left][right]=x;
    }

    int countOfPairs(vector<int>&nums){
       int n=nums.size();
        vector<vector<vector<int>>>dp(n, vector<vector<int>>(51, vector<int>(51,-1)));

       return solve(n,0,0,50,dp,nums);
    }
};
