class Solution {
    int mod = 1_000_000_007;

    public int countOfPairs(int[] nums) {
        int n = nums.length;
        long[][] dp = new long[n][51];
        for(int i=nums[n-1]; i>=0; i--) {
            dp[n-1][i]=1;
        }

        for(int i=n-2; i>=0; i--) {
            for(int a = nums[i]; a>=0; a--) {
                int b = nums[i]-a;
                for(int c=a; c<=nums[i+1]; c++) {
                    int d = nums[i+1]-c;
                    if(d<=b) {
                        dp[i][a] += dp[i+1][c];
                        dp[i][a] %= mod;
                    }
                }
            }
        }

        int res = 0;
        for(int i=0; i<=nums[0]; i++) {
            res += dp[0][i];
            res %= mod;
        }

        return res;
    }
}
