import java.util.*;

public class Solution {
    private static final int MOD = 1000000007;
    private long[][][] dp;

    public int countOfPairs(int[] nums) {
        int size = nums.length;
        dp = new long[size][51][51];
        for (int i = 0; i < size; i++) {
            for (int j = 0; j < 51; j++) {
                Arrays.fill(dp[i][j], -1);
            }
        }
        return (int) solve(0, 0, nums[0], nums);
    }

    private long solve(int index, int a, int b, int[] nums) {
        if (index >= nums.length) return 1;
        if (dp[index][a][b] != -1) return dp[index][a][b];

        long result = 0;
        for (int i = a; i <= nums[index]; i++) {
            for (int j = b; j >= 0; j--) {
                if (i + j == nums[index]) {
                    result = (result + solve(index + 1, i, j, nums)) % MOD;
                }
            }
        }
        dp[index][a][b] = result;
        return result;
    }
}
