class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        @lru_cache(None)
        def countPairs(i, prev1):
            if i == n:
                return 1

            total_count = 0
            for arr1_i in range(prev1, nums[i] + 1):
                arr2_i = nums[i] - arr1_i
                if i == 0 or arr2_i <= nums[i-1] - prev1:
                    total_count = (total_count + countPairs(i + 1, arr1_i)) % (pow(10,9)+7)

            return total_count

        return countPairs(0, 0)
