const int MOD= 1e9+7;
#define ll long long int
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n= nums.size();
        vector<vector<vector<ll>>>dp(n,vector<vector<ll>>(51,vector<ll>(51,-1)));
        function<ll(int, int, int)> solve= [&](int id, int v1, int v2)->ll {
            if(id>=n) return 1;
            ll res=0;
            if(dp[id][v1][v2]!=-1) return dp[id][v1][v2];
            for(int i=v1;i<=nums[id];i++){
                for(int j=v2;j>=0;j--){
                    if(i+j==nums[id]){
                        res= (res+solve(id+1,i,j))%MOD;

                    }
                }
            }
            return dp[id][v1][v2]=res;
        };
        return solve(0,0,nums[0]);
    }
};
