class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        this->nums = nums;
        n = nums.size();
        memo = vector<vector<vector<int>>>(n, vector<vector<int>>(50 + 1, vector<int>(50 + 1, -1)));
        return dfs(0, 0, 50);
    }
private:
    int dfs(int i, int low, int up) {
        if (i == n) {
            return 1;
        }

        int &res = memo[i][low][up];
        if (res != -1) {
            return res;
        }

        res = 0;
        for (int u = 0; u <= up; ++u) {
            if (nums[i] - u < low) {
                break;
            }
            res += dfs(i + 1, nums[i] - u, u);
            res %= M;
        }
        return res;
    }
    vector<int> nums;
    int n;
    int M = 1e9 + 7;
    vector<vector<vector<int>>> memo;
};
