class Solution {
public:
    long long int mod = 1e9+7;
    vector<vector<vector<long long int>>>dp;
    long long int solve(int i, int pre, int pre2, vector<int> &nums){
        int n=nums.size();
        if(i==n) return 1;
        if(dp[i][pre][pre2] != -1) return dp[i][pre][pre2];
        long long int res = 0;
        for(int curr = pre;curr <= min(50, nums[i]);curr++){
            int rem = nums[i]-curr;
            if(rem <= pre2){
                res += solve(i+1, curr, rem, nums)%mod;
            }
        }
        return dp[i][pre][pre2]=res%mod;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        dp.resize(n, vector<vector<long long int>>(51, vector<long long int>(51, -1)));
        long long int ans = solve(0, 0, 50, nums)%mod;
        return (int)ans;
    }
};
