class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        n = len(nums)
        dp = {}
        def recurse(idx, pre1, pre2):
            if (idx, pre1, pre2) in dp:
                return dp[(idx, pre1, pre2)]
            if idx == n:
                return 1

            res = 0
            for i in range(pre1, nums[idx]+1):
                if nums[idx]-i <= pre2:
                    res += recurse(idx+1, i, nums[idx]-i)%MOD

            dp[(idx, pre1, pre2)] = res
            return res

        return recurse(0, 0,  float('inf'))%MOD
