class Solution {
public:
    const int MOD=1e9+7;
    int countValidPairs(vector<int>& nums, int index, int arr1_last, int arr2_last, vector<vector<vector<int>>>& memo) {
    if (index == nums.size()) return 1;

    if (memo[index][arr1_last][arr2_last] != -1) return memo[index][arr1_last][arr2_last];

    int count = 0;
    for (int i = arr1_last; i <= nums[index]; ++i) {
        int j = nums[index] - i;
        if (j <= arr2_last) {
            count = (count + countValidPairs(nums, index + 1, i, j, memo)) % MOD;
        }
    }

    return memo[index][arr1_last][arr2_last] = count;
}
