const int N = 1005;
const int MOD = (int)1e9 + 7;
int dp[N], odp[N], sdp[N];
class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        int n = nums.size(), pre = (int)1e9+5;
        for (int i=0;i<N;++i) dp[i] = 0;
        dp[0] = 1;
        for (auto x : nums) {
            sdp[0] = dp[0];
            dp[0] = 0;
            for (int j=1;j<N;++j) {
                sdp[j] = sdp[j-1] + dp[j];
                if (sdp[j] >= MOD) sdp[j] -= MOD;
                dp[j] = 0;
            }
            for (int i=0;i<=x;++i) {
                // (i, x - i)
                // j <= i; pre - j >= x - i;
                // j <= i and j <= pre + i - x
                int arr = min(i, pre + i - x);
                int ell = 0;
                if (ell <= arr) {
                    dp[i] = sdp[arr] - (ell > 0 ? sdp[ell-1] : 0);
                    if (dp[i] < 0) dp[i] += MOD;
                }
                // cout << dp[i] << " ";
            }
            // cout << '\n';
            pre = x;
        }
        int ans = 0;
        for (int j=0;j<N;++j) {
            ans += dp[j];
            if (ans >= MOD) ans -= MOD;
        }
        return ans;
    }
};
