class Solution {
public:
    int memo(vector<vector<int> >& dp,int n,int prev,int i,vector<int>&nums){
        if(i==n)
        return 1;
        if(dp[i][prev]!=-1)
        return dp[i][prev];
        int sprev=-1;
        if(i==0)
        sprev=nums[i];
        else
        sprev=nums[i-1]-prev;
        long long ans=0;
        long long mod=1e9+7;
        for(int k=max(prev,nums[i]-sprev);k<=nums[i];k++){
            ans+=memo(dp,n,k,i+1,nums);
            ans%=mod;
        }
        return dp[i][prev]=ans;

    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        vector<vector<int> >dp(n,vector<int>(52,-1));
       return memo(dp,n,0,0,nums);
    }
};
