class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        const int mod = 1e9+7;
        int n = nums.size(), ans = 0;
        if(n == 1)
            return nums.back()+1;
        int dp[2000][51] = {0};
        for(int j = 0; j <= nums[n-1]; j++) 
            dp[n-1][j] = 1;

        for(int i = n-2; i >= 0; i--) {
            for(int j = 0; j <= nums[i]; j++) {
                for(int k = j; k <= nums[i+1]; k++) {
                    if(nums[i]-j >= nums[i+1]-k)
                        dp[i][j] = (0LL + dp[i][j] + dp[i+1][k])%mod;
                }
                if(i == 0) 
                    ans = (0LL + ans + dp[0][j])%mod;
            }
        }
        return ans;
    }
};
