class Solution {
public:
    int dp[2001][51];
    long long solve(vector<int> &nums, int ind, int last_val)
    {
        if(dp[ind][last_val]!=-1)
            return dp[ind][last_val];
        if(ind>=nums.size())
        {
            return 1;
        }

        long long ans=0;
        for(int i=last_val;i<=nums[ind];i++)
        {
            if(ind==0 || (ind>0 && nums[ind]-i<=nums[ind-1]-last_val))
                ans = (ans +(long long)solve(nums, ind+1, i))%1000000007;
        }
        dp[ind][last_val] = ans;
        return dp[ind][last_val];
    }

    int countOfPairs(vector<int>& nums) {
        memset(dp, -1, sizeof(dp));
        return (int)solve(nums, 0, 0);
    }
};
