class Solution {
public:
    static const int M = 1'000'000'007;
    vector<vector<vector<int>>> d;

    void i(int n) {
        d = vector<vector<vector<int>>>(n, vector<vector<int>>(51, vector<int>(51, -1)));
    }

    int f(vector<int>& a, int x, int p1, int p2) {
        if (x >= a.size()) return 1;

        if (d[x][p1][p2] != -1) return d[x][p1][p2];

        int r = 0;
        for (int i = 0; i <= a[x]; i++) {
            int v1 = i;
            int v2 = a[x] - i;
            if (v1 >= p1 && v2 <= p2) {
                r = (r + f(a, x + 1, v1, v2)) % M;
            }
        }

        d[x][p1][p2] = r;
        return r;
    }
     int countOfPairs(vector<int>& a) {
        int n = a.size();
        i(n);
        return f(a, 0, 0, 50);
    }
};
