class Solution {
    const int MOD = 1e9 + 7;
    // int ans;
    unordered_map<int, unordered_map<int, int>> m;
    int solve(const vector<int> &nums, const int i, const int prev)
    {
        if (i == nums.size())
        {
            // ans = (ans + 1) % MOD;
            return 1;
        }
        if (m.count(i) > 0 && m[i].count(prev) > 0)
        {
            return m[i][prev];
        }
        long long ans = 0;
        for (int ii = prev; ii <= nums[i]; ii++)
        {
            if (i > 0 && (nums[i] - ii > nums[i-1] - prev))
                continue;
            ans = (ans + solve(nums, i + 1, ii)) % MOD;
        }
        m[i][prev] = ans;
        return ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        // ans = 0;
        return solve(nums, 0, 0);
        // return ans;
    }
};
