class Solution {
public:
    const int mod = 1e9 + 7;
    int dp[2002][51][51];

    int solveDP(int i, int n, int prev1, int prev2, vector<int> &nums){
        if(i >= n){
            return 1;
        }

        if(dp[i][prev1][prev2] != -1){
            return dp[i][prev1][prev2];
        }

        int cur = 0;
        for(int c1 = prev1; c1 <= nums[i]; c1++){
            for(int c2 = prev2; c2 >= 0; c2--){
                if((c1 + c2) == nums[i]){
                    cur = (cur + solveDP(i+1, n, c1, c2, nums)) % mod;
                }
            }
        }

        return (dp[i][prev1][prev2] = cur);
    }

    int countOfPairs(vector<int>& nums) {
        int n = nums.size();

        memset(dp, -1 ,sizeof(dp));
        return solveDP(0, n, 0, nums[0], nums);
    }
};
