class Solution {
    long mod=(long)(1e9)+7;

    public int countOfPairs(int[] nums) {
        int n=nums.length;
        long[][][] dp=new long[n+1][51][51];

        // dp[0][0][50]=1;
        for(int i=1;i<=n;i++){
            int cur=nums[i-1];
            for(int j=0;j<=cur;j++){
                int left=cur-j;
                if(i==1){
                    dp[i][j][left]=1;
                    continue;
                }
                for(int k=0;k<=j;k++){
                    // for(int m=left;m<=50;m++){

                        int pl=nums[i-2]-k;
                        if(pl<0)break;
                        if(left<=pl)
                        dp[i][j][left]=madd(dp[i-1][k][pl],dp[i][j][left]);


                    // }
                }
                // System.out.println(i+" "+j+" "+left+" "+dp[i][j][left]);
            }
        }
        long ans=0;
        for(int i=0;i<=50;i++){
            for(int j=0;j<=50;j++)ans=madd(ans,dp[n][i][j]);
        }
        return (int)ans;
    }

    public long madd(long a,long b){
        return (a%mod +b%mod)%mod;
    }
}
