class Solution {
public:

    #define ll long long
    #define MOD ((ll) 1e9+7)

    ll dp[2001][1004];

    ll dfs(int i, int j, vector<int>& nums) {

        if(i == nums.size())
            return 1;

        if(dp[i][j] != -1)
            return dp[i][j];

        dp[i][j] = 0;

        int a = j, b = nums[i-1] - j;

        for(int k=a; k<=nums[i]; ++k)
            if(nums[i]-k <= b && nums[i]-k >= 0) {
                dp[i][j] += dfs(i+1, k, nums);
                dp[i][j] %= MOD;    
            }

        return dp[i][j];

    }


    int countOfPairs(vector<int>& nums) {

        memset(dp, -1, sizeof(dp));

        ll res = 0;

        for(int i=0; i<=nums[0]; ++i) {
            res += dfs(1, i, nums);
            res %= MOD;   
        }

        return res;

    }
};
