class Solution {
    int solve(vector<int>& nums,int i,int inc,int dec,vector<vector<vector<int>>> &dp){
        if(i>=nums.size())
            return 1;
        if(dp[i][inc][dec]!=-1)
            return dp[i][inc][dec];
        int ans=0;
        for(int idx=inc;idx<=nums[i];idx++){
            if(nums[i]-idx<=dec){
                ans=((long)ans+solve(nums,i+1,idx,nums[i]-idx,dp))%1000000007;
            }
        }
        return dp[i][inc][dec]=ans;
    }
public:
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size(),vector<vector<int>>(51,vector<int>(51,-1)));
        return solve(nums,0,0,50,dp);
    }
};
