class Solution {
public:
    int mod=1e9+7;

    int helper(int i,int prev1,int prev2,vector<int>& nums,vector<vector<int>> &dp)
    {
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev1]!=-1) return dp[i][prev1]%mod;
        int ans=0;
        for(int j=prev1;j<=nums[i];j++)
        {
            if(nums[i]-j>=0 and nums[i]-j<=prev2){
                ans=(ans+(helper(i+1,j,nums[i]-j,nums,dp)%mod))%mod;
            }
        }
        return dp[i][prev1]=ans%mod;
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<int>> dp(2001,vector<int> (51,-1));
        helper(0,0,50,nums,dp);
        return dp[0][0];
    }
};
