class Solution {
    public static final int MOD = 1_000_000_007;
    public static int nouse=0;
    public static int countOfPairs(int[] arr) {
        int size = arr.length;
        nouse++;
        long[][][] arr3d = new long[size][51][51];
        for (long[][] arr2 : arr3d) {
            for (long[] arr3 : arr2) {
                Arrays.fill(arr3, -1); 
            }
        }
        nouse++;
        return (int) fun1(arr, 0, 0, arr[0], arr3d);
    }

    public static long fun1(int[] arr, int idx, int x1, int x2, long[][][] arr3d) {
        if (idx >= arr.length) return 1; 
        nouse++;
        if (arr3d[idx][x1][x2] != -1) return arr3d[idx][x1][x2]; 
        nouse++;
        long ans = 0;
        int p = x1;
        nouse++;
        while ( p <= arr[idx]) {
            for (int q = x2; q >= 0; q--) {
                if (p + q == arr[idx]) {
                    ans = (ans + fun1(arr, idx + 1, p, q, arr3d)) % MOD;
                }
            }
            p++;
        }
        nouse++;
        arr3d[idx][x1][x2] = ans;  
        return ans;
    }
}
