func countOfPairs(nums []int) int {
    n := len(nums)
    arr := make([][][]int64, n)
    for i,_ := range arr {
        arr[i] = make([][]int64, 51)
        for j,_ := range arr[i] {
            arr[i][j] = make([]int64, 51)
            for k,_ := range arr[i][j] {
                arr[i][j][k] = -1
            }
        }
    } 
    return int(find(nums, 0, 0, nums[0], arr))
}

func find(nums []int, ind int, v1 int, v2 int, arr [][][]int64) int64 {
    if ind >= len(nums) {
        return int64(1)
    }
    if arr[ind][v1][v2] != -1 {
        return int64(arr[ind][v1][v2])
    }
    var answer int64 = 0
    for i:=v1; i<=nums[ind]; i++ {
        for j:=v2; j>=0; j-- {
            if i + j == nums[ind] {
                answer = (answer + find(nums, ind+1, i, j, arr) )% 1000000007
            }
        }
    }
    arr[ind][v1][v2] = answer
    return answer
}
