class Solution {
    public int countOfPairs(int[] nums) {
        final int MOD = 1_000_000_007;
        int n = nums.length;
        int maxValue = 50;
        int[][] dpTable = new int[maxValue + 1][maxValue + 1];
        int[][] sumPrefix = new int[maxValue + 1][maxValue + 1];

        for (int x = 0; x <= nums[0]; x++) {
            int y = nums[0] - x;
            if (y >= 0 && y <= maxValue) {
                dpTable[x][y] = 1;
            }
        }

        for (int x = 0; x <= maxValue; x++) {
            for (int y = 0; y <= maxValue; y++) {
                sumPrefix[x][y] = dpTable[x][y];
                if (x > 0) sumPrefix[x][y] = (sumPrefix[x][y] + sumPrefix[x - 1][y]) % MOD;
                if (y > 0) sumPrefix[x][y] = (sumPrefix[x][y] + sumPrefix[x][y - 1]) % MOD;
                if (x > 0 && y > 0) sumPrefix[x][y] = (sumPrefix[x][y] - sumPrefix[x - 1][y - 1] + MOD) % MOD;
            }
        }

        for (int i = 1; i < n; i++) {
            int[][] newDpTable = new int[maxValue + 1][maxValue + 1];
            int[][] newSumPrefix = new int[maxValue + 1][maxValue + 1];

            for (int x = 0; x <= nums[i]; x++) {
                int y = nums[i] - x;
                if (y < 0 || y > maxValue) continue;

                int previousSum = sumPrefix[x][maxValue];
                if (y > 0) previousSum = (previousSum - sumPrefix[x][y - 1] + MOD) % MOD;

                newDpTable[x][y] = previousSum % MOD;
            }

            for (int x = 0; x <= maxValue; x++) {
                for (int y = 0; y <= maxValue; y++) {
                    newSumPrefix[x][y] = newDpTable[x][y];
                    if (x > 0) newSumPrefix[x][y] = (newSumPrefix[x][y] + newSumPrefix[x - 1][y]) % MOD;
                    if (y > 0) newSumPrefix[x][y] = (newSumPrefix[x][y] + newSumPrefix[x][y - 1]) % MOD;
                    if (x > 0 && y > 0) newSumPrefix[x][y] = (newSumPrefix[x][y] - newSumPrefix[x - 1][y - 1] + MOD) % MOD;
                }
            }
            dpTable = newDpTable;
            sumPrefix = newSumPrefix;
        }

        int totalPairsCount = 0;
        for (int x = 0; x <= maxValue; x++) {
            for (int y = 0; y <= maxValue; y++) {
                totalPairsCount = (totalPairsCount + dpTable[x][y]) % MOD;
            }
        }

        return totalPairsCount;
    }
}
