class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        mod = 10 ** 9 + 7

        @cache
        def dp(idx, prev):
            if idx == len(nums):
                return 1

            res = 0
            num = nums[idx - 1] - prev if idx > 0 else inf
            for nxt in range(prev, nums[idx] + 1):
                if nums[idx] - nxt <= num:
                    res = (res + dp(idx + 1, nxt)) % mod
            return res

        return dp(0, 0)
