    long long cnt=0;
    int mod=1e9+7;
    long long dp[2005][56][56];
    int solve(int i,int prev_inc_val,int prev_dec_val,vector<int>& nums){
        if(i==nums.size()){
            return 1;
        }
        if(dp[i][prev_inc_val][prev_dec_val]!=-1){
            return dp[i][prev_inc_val][prev_dec_val];
        }

        // setting inc values
        int start=prev_inc_val;
        long long ans=0;
        for(int j=start;j<=nums[i];j++){
            int inc_val=j;
            int dec_val=nums[i]-inc_val;

            if(inc_val>=prev_inc_val and dec_val<=prev_dec_val){
                ans= (ans%mod + solve(i+1,inc_val,dec_val,nums)%mod + mod)%mod;
            }
        }

        return dp[i][prev_inc_val][prev_dec_val]=ans;
    }
    int countOfPairs(vector<int>& nums) {
        int n=nums.size();
        memset(dp,-1,sizeof dp);

        return solve(0,0,55,nums)%mod;

