class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        MOD = 10**9+7
        n = len(nums)
        @cache
        def solve(ind, l1, l2):
            if ind == n:
                return 1
            num = nums[ind]
            ans = 0
            val1 = 0 if l1 is None else l1
            val2 = num - val1
            while val2 >= 0:
                if l2 is None or val2 <= l2:
                    ans += solve(ind+1, val1, val2)
                    ans %= MOD
                val1+=1
                val2 = num - val1
            return ans
        return solve(0, None, None)
