class Solution {
public:
    int countOfPairs(std::vector<int>& nums) {
        int n = nums.size();
        std::vector<std::vector<std::vector<long long>>> dpr(n, std::vector<std::vector<long long>>(51, std::vector<long long>(51, -1)));
        long long mdf = 1e9 + 7;

        std::function<long long(int, int, int)> rcr = [&](int idx, int a, int b) -> long long {
            if (idx >= nums.size()) return 1;
            long long rslt = 0;

            if (dpr[idx][a][b] != -1) return dpr[idx][a][b];

            for (int x = a; x <= nums[idx]; x++) {
                for (int y = b; y >= 0; y--) {
                    if (x + y == nums[idx]) {
                        rslt += rcr(idx + 1, x, y);
                        rslt %= mdf;
                    }
                }
            }

            return dpr[idx][a][b] = rslt;
        };

        return rcr(0, 0, nums[0]);
    }
};
