class Solution {
public:
    int countOfPairs(vector<int>& nums) {
        constexpr int M = 1000000007;
        // dp[i][x][y] = sum dp[i-1][i in 0..x][j in y..50]
        //               for x + y == nums[i]
        using T = map<pair<int,int>, int>;
        T dp;
        dp[{0,50}] = 1;
        for (int r : nums) { // 2000
            T curr;
            for (int x = 0; x <= r; x++) { // 50
                int y = r - x;
                int cnt = 0;
                for (auto &[p, c] : dp) { // 50
                    auto [i, j] = p;
                    if (i > x || j < y) continue;
                    auto &toadd = curr[{x,y}];
                    toadd = (toadd + c) % M;
                }
            }
            dp = curr;
        }
        int ans = 0;
        for (auto &[_, c] : dp)
            ans = (ans + c) % M;
        return ans;
    }
};
