#define mod (int)(1e9 + 7)
class Solution {
public:
    int solve(int idx, vector<int>& nums, int incPrev, int decPrev, vector<vector<vector<int>>> &dp){
        if(idx == nums.size())
            return 1;

        if(dp[idx][incPrev][decPrev] != -1) return dp[idx][incPrev][decPrev];

        int inc, dec;
        int ans = 0;
        for(int i = 0; i <= nums[idx]; i++){
            inc = i;
            if(incPrev > inc) continue;
            dec = nums[idx] - i;
            if(decPrev < dec) continue;
            ans = ((ans%mod) + (solve(idx + 1, nums, inc, dec, dp)%mod))%mod;
        }
        return dp[idx][incPrev][decPrev] = (ans%mod);
    }
    int countOfPairs(vector<int>& nums) {
        vector<vector<vector<int>>> dp(nums.size() + 1, vector<vector<int>>(51, vector<int>(51, -1)));
        return solve(0, nums, 0, 50, dp);
    }
};
