class Solution {
public:
unordered_map<int,unordered_map<int,unordered_map<int,int>>> dp;
int M = 1e9+7;
    long long solveDP(int i, vector<int>& nums, int prv1 , int prv2){
        if( i == nums.size())return 1;
        if(dp.count(i) and dp[i].count(prv1) and dp[i][prv1].count(prv2))return dp[i][prv1][prv2];
        long long ans =0;
        for(int k = 0 ;  k <= nums[i] ; k++){
            int p1 = k;
            int p2 = nums[i] - k;
            if(p1 >= prv1 and p2 <= prv2){
                ans = (ans%M + solveDP(i+1,nums,p1,p2)%M)%M;
            }
        }
        return dp[i][prv1][prv2] = ans%M;
    }
    int countOfPairs(vector<int>& nums) {
        return solveDP(0,nums,0,50)%M;
    }
};
