class Solution:
    def countOfPairs(self, nums: List[int]) -> int:
        n = len(nums)
        MOD = 10**9 + 7
        dp = {}
        def count(ind, prev1):
            if ind == n:
                return 1

            if (ind, prev1) in dp: return dp[(ind, prev1)]

            total = 0
            for i in range(prev1, nums[ind]+1):
                second = nums[ind] - i
                if ind > 0:
                    prev2 = nums[ind-1] - prev1
                else:
                    prev2 = math.inf
                if second <= prev2:
                    total += count(ind+1, i)

            dp[(ind, prev1)] = total % MOD
            return dp[(ind, prev1)]

        return count(0, 0)
